package Document;
public class GreatestNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int n1=22;
		int n2=33;
		int n3=44;
		if(n1>n2&&n1>n3)
		{
		System.out.println("n1 is greatest");
		}else
		if(n2>n1&&n2>n3)
		{
		System.out.println("n2 is greatest");
		}else
		if(n3>n1&&n3>n2)
		{
		System.out.println("n3 is greatest");
		}
	}

}
